import React, { Component } from 'react';
import { Route } from 'react-router-dom';
import { withRouter } from 'react-router';
import './App.css';
import UserRegisterForm from './components/UserRegisterForm';
import UserLoginForm from './components/UserLoginForm';
import { registerUser,
        loginUser,
        getTherapists,
        getOneTherapist,
        getUserAppointments,
        createUserAppointment,
        editUserAppointment,
        deleteUserAppointment, } from './services/users';
import UserProfile from './components/UserProfile'
import TherapistList from './components/TherapistList'
import TherapistProfile from './components/TherapistProfile'
import Modal from './components/Modal';
import AppointmentForm from './components/AppointmentForm';

class App extends Component {
  constructor(){
    super()

    this.state = {
      therapist: '',
      appointments: [],
      therapists: [],
      date: '',
      time: '',
      user_first_name: '',
      user_last_name: '',
      user_email: '',
      password: '',
      modalOpen: false
    }
    this.handleLogin = this.handleLogin.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.handleRegister = this.handleRegister.bind(this)
    this.handleLogout = this.handleLogout.bind(this)
    this.getAppointments = this.getAppointments.bind(this)
    this.getAllTherapists = this.getAllTherapists.bind(this)
    this.getTherapist = this.getTherapist.bind(this)
    this.toggleModal = this.toggleModal.bind(this);
    this.createAppointment = this.createAppointment.bind(this);
  }

  toggleModal() {
    this.setState(prevState => ({
      modalOpen: !prevState.modalOpen
    }));
  }

  handleLogout(e) {
  e.preventDefault();
  this.setState({
      user_first_name: '',
      user_last_name: '',
      user_email: '',
      password: ''
  })
  this.props.history.push('/');
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    })
  };

  async handleRegister(ev) {
    ev.preventDefault();
    const { user_first_name, user_last_name, user_email, password } = this.state
    if ( user_first_name && user_last_name
      && user_email && password ) {
      try {
        const newUser = {
          user_first_name: this.state.user_first_name,
          user_last_name: this.state.user_last_name,
          user_email: this.state.user_email,
          password: this.state.password
        }
        const data = await registerUser(newUser);
        console.log(this.state);
        console.log(data);
        this.setState({
            user_first_name: '',
            user_last_name: '',
            user_email: '',
            password: ''
        })
        this.props.history.push(`/profile`);
      } catch (e) {
        console.log(e, 'Something went wrong...');
      }
    } else {
      console.log('please fill out form');
      // logic works to not allow partially filled out forms to dispatch server call
      // need to know conditional render "please fill out form" as html if failure
    }
  };

  async handleLogin(ev) {
    ev.preventDefault();
    const { user_email, password } = this.state;
    if (user_email && password) {
      try {
        const resp = await loginUser(this.state)
        if(resp) {
          console.log(resp);
          this.setState({
              user_email: '',
              password:''
          })
          this.props.history.push(`/profile`);
        } else {
          console.log('wrong username or password');
          // this needs user prompt so they can understand why login failed
        }
      } catch(e) {
        console.log(e, 'Someting went wrong...');
      }
    } else {
      console.log('please put both a username and password');
    };
  };

  async getAppointments(){

    const data ={
      time: this.state.time,
      date: this.state.date
    }
    const appointments = await getUserAppointments(data)
    console.log(appointments);
    this.setState({
      appointments
    })
  }

  async getAllTherapists(){
    const therapists = await getTherapists()

    this.setState({
      therapists
    })
  }

  async getTherapist(therapistId){
    const therapist = await getOneTherapist(therapistId)
    console.log(therapist);
    this.setState({
      therapist
    })
    this.props.history.push('/therapist');
  }

  async createAppointment(e){
    e.preventDefault();
    const data = {
      date: this.state.date,
      time: this.state.time,
    }
    const appointment = await createUserAppointment(data)
    this.setState({
      date: '',
      time: ''
    })
  }

  async componentDidMount(){
    await this.getAppointments();
    await this.getAllTherapists();
  }

  render() {
    return (
      <div className="App">
        <Route exact path ='/' render={(props) => (
          <div>
            <UserRegisterForm
            {...props}
              handleChange={this.handleChange}
              handleSubmit={this.handleRegister}
              first_name={this.state.first_name}
              last_name={this.state.last_name}
              email={this.state.email}
              password={this.state.password}
              closeModal={this.toggleModal}/>
          </div>
        )} />

        <Route exact path='/scheduleAppointment' render={(props) => (
          <div>
            <AppointmentForm
            {...props}
            handleChange={this.handleChange}
            handleSubmit={this.createAppointment}
            date={this.state.date}
            time={this.state.time} />
          </div>
        )} />

        <Route exact path='/login' render={(props) => (
          <div>
            <UserLoginForm
              {...props}
              handleChange={this.handleChange}
              handleSubmit={this.handleLogin}
              email={this.state.email}
              password={this.state.password}
              closeModal={this.toggleModal}
              isOpen={this.state.modalOpen}/>
          </div>
        )} />

        <Route exact path='/profile' render={(props) => (
          <div>
            <UserProfile appointments={this.state.appointments} />
          </div>
        )}/>
        <Route exact path='/therapists' render={(props) => (
          <div>
            <TherapistList getTherapist={this.getTherapist} therapist={this.state.therapist} therapists={this.state.therapists} />
          </div>
        )} />
        <Route exact path='/therapist' render={(props) => (
          <div>
            <TherapistProfile therapist={this.state.therapist}/>
          </div>
        )} />

        <Modal
          closeModal={this.toggleModal}
          isOpen={this.state.modalOpen} />
      </div>
    );
  }
}

export default withRouter (App);
