const getUserAppointments = async () => {
  try {
    const resp = await api.get(`/users/1/appointments/`)
    console.log(resp.data);
    return resp.data;
  } catch (e) {
    console.error(e.message);
  }
}

const createUserAppointment = async (userId, appointment) => {
  try {
    const resp = await api.post(`/users/${userId}/appointments`, appointment)
    return resp.data
  } catch(e) {
    console.log(e);
    return false;
  }
}

const editUserAppointment = async (userId, appointmentId, appointment) => {
  try {
    const resp = await api.put(`/users/${userId}/appointments/${appointmentId}`, appointment);
    return resp.data
  } catch(e) {
    console.log(e);
    return false;
  }
}

const deleteUserAppointment = async (userId, appointmentId) => {
  try {
    const resp = await api.delete(`/users/${userId}/appointments/${appointmentId}`)
    return resp.data.message
  } catch(e) {
    console.log(e);
    return false
  }
}


export {
  registerUser,
  loginUser,
  getUserAppointments,
  createUserAppointment,
  editUserAppointment,
  deleteUserAppointment,
}
